import React from 'react';
import Sidebar from "./Sidebar";
import {useLocation} from 'react-router-dom';
import { useState , useEffect } from "react";
import { db } from '../firebase-config';
import { collection, doc, getDocs } from 'firebase/firestore';
function ViewProfile(){
    const location=useLocation();
    const [users, setUsers] = useState([]);
    const [mail,setMail] = useState(location.state.id);
    const fetchData = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "doctors"));
      querySnapshot.forEach((doc) => {
        if (mail === doc.data().email) {
          setUsers((prev) => [...prev, doc.data()]);
        }
      });
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    fetchData();
  }, []);
    return(
        <>
        <Sidebar>
        <div className="search-main">
      <div className="search-heading">View Profile</div>
      {users.map((item, key) => (
        item.address.map(
            (itm, k) => (
            <div>
                <p>Dr. {item.displayName?? item.name} ,{mail},{item.phoneNumber?? item.contact},{itm[0].streetNumber}, {itm[0].street}, {itm[0].district}, {itm[0].city}, {itm[0].region}</p>
             

    
            
            </div>

  
            )
          )
      ))}

   </div>
        </Sidebar>
        
        </>

    )
}
export default ViewProfile;
