import { collection, getDocs } from "firebase/firestore";
import { doc, setDoc } from "firebase/firestore";
import { query, where } from "firebase/firestore";
import { useLocation } from 'react-router-dom';
import { authentication, db } from '../firebase-config';
import { useState, useEffect } from "react";
import Messenger from "./Messenger";
import { Link } from "react-router-dom";
import { FaArrowCircleLeft } from "react-icons/fa";
import { MdSend } from "react-icons/md";
function Message() {
  const location = useLocation();
  const [messages, setMessages] = useState([]);
  const [temp, setTemp] = useState([]);
  const querySnapshot = async () => {
    console.log("State", location.state.docid);
    const col = collection(db, "chatroom");
    const q = query(
      col,
      where(
        "chatId",
        "==",
        location.state.userid + location.state.docid
      )
    );
    const snap = await getDocs(q);
    let messageArraay = [];
    snap.forEach((doc) => {
      const new_obj = {
        ...doc.data(),
        //createdAt: doc.data().createdAt.toDate().getTime(),
      };

      messageArraay.push(new_obj);
    });
    console.log("message array", messageArraay);
    setMessages(messageArraay.reverse());
    console.log("msgs array", messages)
  };
  useEffect(() => {
    //console.log(props.route);
    querySnapshot();
  }, []);
  const onSend = (messageArray) => {
    const msg = messageArray[0];
    const mymsg = {
      ...msg,
      sentBy:
        location.state.current == "doctor"
          ? location.state.docid
          : location.state.userid,
      sentTo:
        location.state.current == "doctor"
          ? location.state.userid
          : location.state.docid,

      craetedAt: new Date(),
      chatId:
        location.state.userid + location.state.docid,
    };
    //setMessages((previousMessages) =>
    // setTemp((prev) => [...prev, mymsg]);
    //temp.append(previousMessages,mymsg);
    //GiftedChat.append(previousMessages, mymsg)
    //console.log("set messages"+temp);
    //);
    setMessages((previousMessages) =>
      setTemp((previousMessages) => [...previousMessages, mymsg])
    );
    //console.log("temppp",temp);
    const docid =
      location.state.docid > location.state.userid
        ? location.state.userid +
        "-" +
        location.state.docid
        : location.state.docid +
        "-" +
        location.state.userid;

    const myDoc = doc(db, "chatroom", new Date().toString());
    setDoc(myDoc, mymsg)
      .then(() => {
        console.log("Document written with ID: ", myDoc.id);
      })
      .catch((error) => {
        console.error("Error adding document: ", error.message);
      });
  };


  console.log("Doc id :", location.state.docid)
  console.log("User id", location.state.userid)
  console.log("temppp", temp);
  return (
    <>

      <div className="messenger">
        <Messenger />
        <div className="chatBox">
          <div className="chatBoxWrapper">
       
              <div className='chatBoxTop'>
              {messages.map((item, key) => (
                 <div className="message">  
                <div className="messageTop">
                  <p className="messageText">{item.text} </p>
                
                </div></div>
              ))}
              </div>
             
              <div className="chatBoxBottom">
                <textarea className="chatMessageInput" placeholder="Write something.."></textarea>
                <button className="chatSubmitButton" onClick={(messages)=>onSend(messages)}>
                  <div className='sendicon'>  <MdSend /></div>
                </button>
              </div>
          </div>
        </div>

      </div>
      <Link to='/home'>
        <div className='backtohome'><FaArrowCircleLeft /></div></Link>




    </>

  );

}
export default Message;
