import React from 'react';
import Sidebar from "./Sidebar";
import {useLocation} from 'react-router-dom';
import { useState , useEffect } from "react";
import { db } from '../firebase-config';
import { collection, doc, getDocs } from 'firebase/firestore';
function divProfile(){
const location=useLocation();
const [users, setUsers] = useState([]);
  const [mail,setMail] = useState(location.state.id.route.params.params.param1);
  const fetchData = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "doctors"));
      querySnapshot.forEach((doc) => {
        if (mail === doc.data().email) {
          setUsers((prev) => [...prev, doc.data()]);
        }
      });
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    fetchData();
  }, []);
    return(
        <>
        <Sidebar>
        <div className="search-main">
      <div className="search-heading">View Profile</div>
      {users.map((item, key) => (
        item.address.map(
            (itm, k) => (
            <div>
              <div>
                <p style={{ fontWeight: 'bold', fontSize:18,marginTop:15}}>Name: </p>
                <p  style={{  fontSize:18, marginLeft: 25,marginTop:15}}>Dr. {item.displayName?? item.name} </p>
              </div>

              <div>
                <p style={{ fontWeight: 'bold', fontSize:18,marginTop:15}}>Email: </p>
            <p style={{  fontSize:18, marginLeft: 28,marginTop:15}}>{mail}</p>

            </div>

              
              <div>
                <p style={{ fontWeight: 'bold', fontSize:18,marginTop:15}}>Contact: </p>
            <p style={{  fontSize:18, marginLeft: 10,marginTop:15}}>{item.phoneNumber?? item.contact}</p>
            </div>

            <div>
              <p style={{ fontWeight: 'bold', fontSize:18,marginTop:15}}>Address: </p>
            <p style={{ maxWidth:250, fontSize:15, marginLeft: 7,marginTop:15}}> {itm[0].streetNumber}, {itm[0].street}, {itm[0].district}, {itm[0].city}, {itm[0].region}</p>
            </div>
            
            
            </div>

  
            )
          )
      ))}


   </div>
        </Sidebar>
        
        </>

    )
}
export default divProfile;
