import React from 'react';
import DoctorSidebar from "./DoctorSidebar";
import { useLocation } from 'react-router-dom';
import { useState, useEffect } from "react";
import { db } from '../firebase-config';
import { collection, getDocs } from 'firebase/firestore';
function ViewProfileOfPatients() {
    const location = useLocation();
    const [users, setUsers] = useState([]);
    const [mail, setMail] = useState(location.state.id);
    console.log(location.state.id);
    const fetchData = async () => {
        try {
            const querySnapshot = await getDocs(collection(db, "users"));
            querySnapshot.forEach((doc) => {
                if (mail === doc.data().email) {
                    setUsers((prev) => [...prev, doc.data()]);
                }
            });
        } catch (err) {
            console.log(err);
        }
    };
    useEffect(() => {
        fetchData();
    }, []);
    return (
        <>
            <DoctorSidebar>
                <div className="search-main">  
                    <div className="search-heading">View Profile Of Patients</div>
                    {users.map((item, key) => ( 
                        
                                <div className='view-patient-main'>
                                    <p>Name : {item.displayName ?? item.name}</p>
                                    <p>Email address: {mail}</p>
                                    <p>Phone Number: {item.phoneNumber ?? item.contact}</p>
                                    <button type="submit" className="c-patient-button">
                                        Chat
                                    </button>
                                    <button type="submit" className="b-patient-button">
                                        Book Appointmnet
                                    </button>
                                </div>                    
                    ))}


                    

                </div>
            </DoctorSidebar>

        </>

    )
}
export default ViewProfileOfPatients;
