import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import AdminSidebar from './AdminSidebar';
import { useState , useEffect } from "react";
//import { getAuth, onAuthStateChanged } from "firebase/auth";
import { db } from '../firebase-config';
import { collection, doc, getDocs } from 'firebase/firestore';
function AdminHome () {
    var Doctors=[];
    const [data, setData] = useState([]);
    //const [email, setEmail]=useState([]);
    const [query, setQuery] = useState('');
    useEffect(() => {
      const querySnapshot = getDocs(collection(db, "doctors")).then((querySnapshot) => {
        querySnapshot.forEach((doc) => {
          // doc.data() is never undefined for query doc snapshots
          console.log("Usersss",doc.id, " => ", doc.data());
          const response = doc.data();
          //const email=doc.data().email;
          Doctors.push(response);
         // setEmail(prev=>[...prev,email])
          setData(prev=>[...prev,response])
          // setPatient(Users);
          // Alert.alert("LoggedIn as Doctor");
          // setDoc("False");
          //console.log("SEARCH RESULTS==="+Doctors)
      });console.log("Doctor's Array :",Doctors)   
      }); 
  }, []);
  var Users=[];
  //const [email, setEmail]=useState([]);
  useEffect(() => {
    const querySnapshot = getDocs(collection(db, "users")).then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        // doc.data() is never undefined for query doc snapshots
        console.log("Usersss",doc.id, " => ", doc.data());
        const response = doc.data();
        //const email=doc.data().email;
        Users.push(response);
       // setEmail(prev=>[...prev,email])
        setData(prev=>[...prev,response])
        // setPatient(Users);
        // Alert.alert("LoggedIn as Doctor");
        // setDoc("False");
        //console.log("SEARCH RESULTS==="+Doctors)
    });console.log("Doctor's Array :",Users)   
    }); 
}, []);

    return (<>
    <AdminSidebar>
    <div className="search-main">
      <div className="search-heading">Search for Users to view their profile</div> 
            <div className="search" >
                <input
                    className="search__input"
                    type="text"
                    id="search"
                    placeholder="Search here..."
                />
                <button className="search__button">
                    <FontAwesomeIcon icon={faMagnifyingGlass} />
                </button>
            </div>
            <ul className="search-results">
         {data.filter((user) =>
           user.displayName.toLowerCase().includes(query)
         ).map((user) => (
           <li className="fetch-doctor" key={user.displayName}>
            {user.email}<br/>{user.displayName}
          </li>
       ))}
       </ul>
       <ul className="search-results">
         {data.filter((user) =>
           user.displayName.toLowerCase().includes(query)
         ).map((user) => (
           <li className="fetch-doctor" key={user.displayName}>
            {user.email}<br/>{user.displayName}
          </li>
       ))}
       </ul>
       
       
        </div></AdminSidebar>
            </>
    )
    }
export default AdminHome
