import { useState } from 'react';
import Calendar from 'react-calendar';
import TimePicker from 'react-time-picker';
import 'react-calendar/dist/Calendar.css';
import { Link } from 'react-router-dom';
import './Calender.css';
import './Clock.css';
import { authentication } from '../firebase-config';
import { useNavigate } from 'react-router-dom';
import {  useEffect } from "react";
import Sidebar from './Sidebar';
//import StripeContainer from './StripeContainer';
function Appointment() {
  //const [showItem, setShowItem] = useState(false);
  const navigate = useNavigate();
  useEffect(()=>{
      if(!authentication.currentUser){
          navigate('/login');
          return <div className='splash'>
              Loading
          </div>
      }
  },[])

  const [date, setDate] = useState(new Date());
  const [time, setTime] = useState("");
  return (<><Sidebar>
    <div className='main-main'>
      <div className='search-heading'>Appointment</div>
      <div className='calendar-container'>
         <h1 className='date-heading'>Select Date</h1>
        <Calendar onChange={setDate} value={date} />
      </div>
      <div className="selected-date">
      Selected date: {date.toDateString()}
   </div>
          <div className='time-container'>
          <h1 className='time-heading'>Select Time</h1>
          <div ><TimePicker className='test' onChange={setTime} value={time} /></div>
          </div>
          <div className="selected-time">
      Selected Time: {time.toString()}
   </div>

   <Link to='/stripeContainer'>
          <button className="Appointment-button"
      >
       <h5 className="book">Book Appointment</h5>
      </button></Link>


    </div></Sidebar>
    </> );
}

export default Appointment;
