import React ,{useState} from 'react'   
import { CardElement, useElements, useStripe } from "@stripe/react-stripe-js"
import axios from "axios"
import  './Paymentform.css';
import succlogo from "../assets/success.gif";
const CARD_OPTIONS = {
	iconStyle: "solid",
	style: {
		base: {
			iconColor: "gray",
			color: "white",
			fontWeight: 700,
			fontFamily: "Roboto, Open Sans, Segoe UI, sans-serif",
			fontSize: "29px",
			fontSmoothing: "antialiased",
			":-webkit-autofill": { color: "#fce883" },
			"::placeholder": { color: "white" }
		},
		invalid: {
			iconColor: "red",
			color: "red"
		}
	}
}

function PaymentForm(){
    const [success, setSuccess ] = useState(false)
    const stripe = useStripe()
    const elements = useElements()
    const handleSubmit = async (e) => {
        e.preventDefault()
        const {error, paymentMethod} = await stripe.createPaymentMethod({
            type: "card",
            card: elements.getElement(CardElement)
        })  
    
    if(!error){
        try{
            const {id} = paymentMethod
            const response = await axios.post("http://localhost:4000/payment", {
                amount: 2000,
                id
            })
            if(response.data.success) {
                console.log("Successful payment")
                setSuccess(true)
            }

        }catch(error){
            console.log("Error", error)
        }
    }
    else {
        console.log(error.message)
    }

}
return(
<div>
{!success ? 
<div className='payment-container'>
        <form onSubmit={handleSubmit}>
            <fieldset className="Form-Group">
                <div className="Form-Row">
                    <CardElement options={CARD_OPTIONS}/>
                </div>
            </fieldset>
            <button className='pay-button'>Pay</button>
        </form></div>
        :
       <div>
           <div><h1 style={{color:'#35414F', marginLeft:690, marginTop:750, position:'absolute',fontFamily: "Arial, Helvetica, sans-serif", fontWeight:"bold"}}>Your booking is confirmed</h1></div>
    <div className="border"> <div className="success-box"> <img src={succlogo} alt="loading..." style={{width:300 ,height:300}}/> </div> </div>
       </div> 
        }
</div>

)}

export default PaymentForm;
