import React, {useState} from 'react';
import { Alert } from "react-bootstrap";
//import Registeration from './Registeration';
import {Link} from 'react-router-dom';
import Footer from '../Components/Footer';
import image from "../images/logo.jpeg";
function Login() {
  const [emaillog, setEmaillog] = useState(" ");
  const [passwordlog, setPasswordlog] = useState(" ");
  const [flag, setFlag] = useState(false);
  const [home, setHome] = useState(true);
  function handleLogin(e) {
    e.preventDefault();
    let pass = localStorage.getItem("sadiaPassword").replace(/"/g, "");
    let mail = localStorage.getItem("sadiaEmail").replace(/"/g, "");
    if (!emaillog || !passwordlog) {
      setFlag(true);
      console.log("EMPTY");
    } else if (passwordlog !== pass || emaillog !== mail) {
      setFlag(true);
    } else {
      setHome(!home);
      setFlag(false);
    }
  }
  return (<>

    <div className='login-main'><Footer/>
    <div className="logo-image"> <img src={image} alt="loading..."  style={{width:1100 ,height:300, marginLeft:20, marginTop:100}} /> </div> 
      <div className='log-inner'> 

        <form onSubmit={handleLogin}>
          <h3 className="heading">Patient's Login</h3>
          <div className="log-group">
            <label className='log-label'>Email</label>
            <input
              type="email"
              className="log-control"
              placeholder="Enter email"
              onChange={(event) => setEmaillog(event.target.value)}
            />
          </div>
          <div className="log-group">
            <label className='log-label'>Password</label>
            <input
              type="password"
              className="log-control"
              placeholder="Enter password"
              onChange={(event) => setPasswordlog(event.target.value)}
            />
          </div>
          <div>
          <Link to='/home'>  
          <button type="submit" className="log-button">
            Login
          </button> </Link></div>
          <div>
            <Link className="login-link" to='/registration'>Register Now</Link>
               </div>

          {flag && (
            <Alert color="primary" variant="warning">
              Invalid email or password!
            </Alert>
          )}
        </form>
        </div>
    </div></>
  )
}
export default Login
