import { collection, getDocs } from "firebase/firestore";
import { doc, setDoc } from "firebase/firestore";
import { query, where } from "firebase/firestore";
import { useLocation } from 'react-router-dom';
import { db } from '../firebase-config';
import { useState, useEffect } from "react";
function Message({own}){
    const temp=[];
    const location = useLocation();
    const [messages, setMessages] = useState([]);

    const querySnapshot = async () => {
      console.log(location.state.docid);
      const col = collection(db, "chatroom");
      const q = query(
        col,
        where(
          "chatId",
          "==",
          location.state.userid + location.state.docid
        )
      );
      const snap = await getDocs(q);
      let messageArraay = [];
      snap.forEach((doc) => {
        const new_obj = {
          ...doc.data(),
          createdAt: doc.data().createdAt.toDate(),
        };
  
        messageArraay.push(new_obj);
      });
      console.log(messageArraay);
      setMessages(messageArraay.reverse());
    };
  
    useEffect(() => {
      //console.log(props.route);
      querySnapshot();
    }, []);
    const onSend = (messageArray) => {
        const msg = messageArray[0];
        const mymsg = {
          ...msg,
          sentBy:
            location.state.current == "doctor"
              ? location.state.docid
              : location.state.userid,
          sentTo:
          location.state.current == "doctor"
              ? location.state.userid
              : location.state.docid,
    
          craetedAt: new Date(),
          chatId:
          location.state.userid +location.state.docid,
        };
        setMessages((previousMessages) =>
        temp.append(previousMessages,mymsg)
      //GiftedChat.append(previousMessages, mymsg)
    );

    const docid =
    location.state.docid > location.state.userid
        ? location.state.userid +
          "-" +
          location.state.docid
        : location.state.docid +
          "-" +
          location.state.userid;

    const myDoc = doc(db, "chatroom", new Date().toString());
    setDoc(myDoc, mymsg)
      .then(() => {
        console.log("Document written with ID: ", myDoc.id);
      })
      .catch((error) => {
        console.error("Error adding document: ", error.message);
      });
  };
return(
<>
<div className={own ? "message own" : "message"}>
<div className="messageTop">
<p className='messageText'>Lorem Ipsum is simply dummy text of the printing and typesetting industry. </p></div>
<div className="messageBottom">1 hour ago</div>
</div>
</>

);

}
export default Message;
