import React from 'react';
import DoctorConversation from "./DoctorConversations";
import {Link} from 'react-router-dom';
import  DoctorMessage from './DoctorMessage';
import {  useState } from "react";
import { FaArrowCircleLeft } from "react-icons/fa";
import { MdSend } from "react-icons/md";
function DoctorMessenger() {
  const [currentChat, setCurrentChat] = useState(<DoctorMessage/>);
return (
   <>
     <div className="messenger">
       <div className="chatMenu">
         <div className="chatMenuWrapper">
           <input placeholder="Search for patients" className="chatMenuInput" />
           <DoctorConversation/>
           <DoctorConversation/>
           <DoctorConversation/>
         </div>
       </div>
       <div className="chatBox">
         <div className="chatBoxWrapper">
         {currentChat ? (<>
       <div className='chatBoxTop'>
         <DoctorMessage/>
    <DoctorMessage own={true}/>
    <DoctorMessage/>

    <DoctorMessage own={true}/>
   

       </div>
       <div className="chatBoxBottom">
   <textarea className="chatMessageInput" placeholder="Write something.."></textarea>
   <button className="chatSubmitButton">
                  <div className='sendicon'>  <MdSend/></div>
                 </button>
</div> </>):(
              <span className="noConversationText">
                Open a conversation to start a chat.
              </span>
            )}
         </div>
       </div>
       
     </div>
     <Link to='/doctorHome'>
     <div className='backtohome'><FaArrowCircleLeft/></div></Link>
   </>
 );
}
export default DoctorMessenger;
