import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import Sidebar from './Sidebar';
import { useState , useEffect } from "react";
//import { getAuth, onAuthStateChanged } from "firebase/auth";
import { db } from '../firebase-config';
import { collection, doc, getDocs } from 'firebase/firestore';
//const auth = getAuth();
function Home () {
  var Doctors=[];
    const [name, setName] = useState([]);
    const [email, setEmail]=useState([]);
    useEffect(() => {
      const querySnapshot = getDocs(collection(db, "doctors")).then((querySnapshot) => {
        querySnapshot.forEach((doc) => {
          // doc.data() is never undefined for query doc snapshots
          console.log("Usersss",doc.id, " => ", doc.data());
          const name = doc.data();
          //const email=doc.data().email;
          Doctors.push(name);
         // setEmail(prev=>[...prev,email])
          setName(prev=>[...prev,name])
          // setPatient(Users);
          // Alert.alert("LoggedIn as Doctor");
          // setDoc("False");
          //console.log("SEARCH RESULTS==="+Doctors)
      });console.log("Doctor's Array :",Doctors)   
      }); 
  }, []);
    function handleSubmit (e)
    { e.preventDefault()}   
    return (<>
    <Sidebar>
    <div className="search-main">
      <div className="search-heading">Search for doctor</div> 
            <div className="search">
                <input
                    className="search__input"
                    type="text"
                    id="search"
                    placeholder="Search here..."
                    //onKeyUp={handleSubmit} 
                    onChange={(e) =>(
                      e.target.value)}
                />
                <button className="search__button">
                    <FontAwesomeIcon icon={faMagnifyingGlass} />
                </button>
            </div>
            <div className="search-results">
      {name.map((user) => (
        <div className="fetch-doctor">{user.email} <br/>{user.displayName}</div>
      ))} 
    </div>
   
               </div></Sidebar>
            </>
    )
    }
export default Home
