import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import Sidebar from './Sidebar';
import { useState , useEffect } from "react";
//import { getAuth, onAuthStateChanged } from "firebase/auth";
import { db } from '../firebase-config';
import { collection, doc, getDocs } from 'firebase/firestore';
//const auth = getAuth();
function Home () {
  var Doctors=[];
    const [data, setData] = useState([]);
    //const [email, setEmail]=useState([]);
    const [query, setquery] = useState('');
    const [state, setstate] = useState({
      query: '',
      list: []
    })
    useEffect(() => {
      const querySnapshot = getDocs(collection(db, "doctors")).then((querySnapshot) => {
        querySnapshot.forEach((doc) => {
          // doc.data() is never undefined for query doc snapshots
          console.log("Usersss",doc.id, " => ", doc.data());
          const response = doc.data();
          //const email=doc.data().email;
          Doctors.push(response);
         // setEmail(prev=>[...prev,email])
          setData(prev=>[...prev,response])
          // setPatient(Users);
          // Alert.alert("LoggedIn as Doctor");
          // setDoc("False");
          //console.log("SEARCH RESULTS==="+Doctors)
      });console.log("Doctor's Array :",Doctors)   
      }); 
  }, []);
  const handleChange = (e) => {
    const results = data.filter(data => {
        if (e.target.value === "") return data;
        return data.email.toLowerCase().includes(e.target.value.toLowerCase())
    })
    setstate({
        query: e.target.value,
        list: results
    })
}
    return (<>
    <Sidebar>
    <div className="search-main">
      <div className="search-heading">Search for doctor</div> 
      <form>   <div className="search">
            <input
                    className="search__input"
                    type="text"
                    id="search"
                    placeholder="Search here..."
                    //onKeyUp={handleSubmit} 
                    onChange={(e) => setQuery(e.target.value.toLowerCase())}
                />
                <button className="search__button">
                    <FontAwesomeIcon icon={faMagnifyingGlass} />
                </button>
            </div></form>
            <ul className="search-results">
      {data.map((user) => (
        <li className="fetch-doctor">{user.email} <br/>{user.displayName}</li>
      ))} 
    </ul>
   
               </div></Sidebar>
            </>
    )
    }
export default Home
