import React from 'react';
import {  useEffect, useState } from "react";

import AdminSidebar from './AdminSidebar';
// import { useLocation } from 'react-router-dom';
import { db,authentication } from '../firebase-config';
import { collection, getDocs, where, query } from 'firebase/firestore';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';


function ViewAppointmentsByAdmin(){
  const [appointments, setAppointments] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(()=>{
    get();
  }, [])

  async function getAppointments(){
    const list =[];
    const docRef = await getDocs(query(collection(db, 'appointments'),where('status', '==', `active`)));
      docRef.forEach((each)=>{
        list.push( {id:each.id, ...each.data()});
      })
      setAppointments(list);
      console.log("Arr",appointments);
      return list;
  };

  async function get(){
    const selectedAppointments = await getAppointments();
  }


   
return( <>
 <AdminSidebar>
    <div className="search-main">
      <div className="search-heading">View Appointments</div>   
      <div className='app-hh'>  <div className='docc-email'>Doctor email</div> <div className='pat-email'>Patient email</div> <div className='appp-date'>Appointment Date</div> <div className='appp-time'>Appointment Time</div>  <div className='appp-status'>Appointment Status</div></div> 
      
      {
         appointments.map((slot)=>
      <div className='appp-main'>   <div className='id-appp'>{slot.doctorId}</div> {slot.patientId} {slot.status}  ,{slot.timeSlot}, { new Date(slot.date.seconds*1000).toDateString()  } </div>
         


         )

      }
             </div></AdminSidebar>
    </>
)

}
export default ViewAppointmentsByAdmin;
