import { useState } from 'react';
import Calendar from 'react-calendar';
import TimePicker from 'react-time-picker';
import 'react-calendar/dist/Calendar.css';
import { Link } from 'react-router-dom';
import './Calender.css';
import './Clock.css';
import { useNavigate } from 'react-router-dom';
import {  useEffect } from "react";
import Sidebar from './Sidebar';
import { useLocation } from 'react-router-dom';
import { db,authentication } from '../firebase-config';
import Moment from 'react-moment';
import moment from 'moment';
import { collection, getDocs } from 'firebase/firestore';
//import StripeContainer from './StripeContainer';

function Appointment() {

  const navigate = useNavigate();
  const location = useLocation();
  const [users, setUsers] = useState([]);
  
  const [date, setDate] = useState(new Date());
  const [time, setTime] = useState("");
  const [slots, setSlots] = useState([]);


  const email =location.state.docid;
  const startTime=location.state.startTime;
  const endTime=location.state.endTime;
  

  useEffect(()=>{
      if(!authentication.currentUser){
          navigate('/login');
          return <div className='splash'>
              Loading
          </div>
      }
      getSlots();
  },[])

  function getSlots(){
    let list = [];
    let x = {
        slotInterval: 30,
        openTime: startTime,
        closeTime: endTime
      };
    let startTime = moment(x.openTime, "HH:mm");
    let endTime = moment(x.closeTime, "HH:mm").add(0, 'days');

    while (startTime < endTime) {
        list.push(startTime.format("HH:mm")); 
        startTime.add(x.slotInterval, 'minutes');
      }
    setSlots(list);
}

  return (<><Sidebar>
    <div className='main-main'>
      <div className='search-heading'>Appointment</div>
      <div className='calendar-container'>
         <h1 className='date-heading'>Select Date</h1>
        <Calendar onChange={setDate} value={date} />
      </div>
      <div className="selected-date">
      Selected date: {date.toDateString()}
   </div>
          <div className='time-container'>
          <h1 className='time-heading'>Select Time</h1>
          <div >{
            
              slots.map((slot)=>{
                  <span style={{ backgroundColor: '#252525'}}>{slot}</span>
              })
          

}</div>
          </div>
          <div className="selected-time">
      Selected Time: {time.toString()}
   </div>

   <Link to='/stripeContainer'>
          <button className="Appointment-button"
      >
       <h5 className="book">Book Appointment</h5>
      </button></Link>


    </div></Sidebar>
    </> );
}

export default Appointment;
